import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import db from '../db/firestore';

const Tasks = () => {

    const [ englishApp_tasks, setEnglishApp_tasks ] = useState<Object[]>()

    useEffect( ()=>{
        db.collection('englishApp_tasks')
        .get()
        .then(result => result.docs)
        .then(docs => docs.map(doc => ({
            id: doc.id, 
            eng: doc.data().eng, 
            lv: doc.data().lv,
            taskDificulty: doc.data().taskDificulty,
            taskType: doc.data().taskType
        })))
        .then(englishApp_tasks => setEnglishApp_tasks(englishApp_tasks))
    }, [])

    console.log(englishApp_tasks.map(task =>{
        task.toLocaleString
    }));
    //show content
    return (
        <View>
        {
            englishApp_tasks?.map(task => <View>
                <Text key="{task.id}">(task.eng)</Text>    
            </View>)
        }
   
        </View>
    );
};

export default Tasks;
