import { logger, fileAsyncTransport } from 'react-native-logs';

// Создание логгера
const log = logger.createLogger({
  transport: fileAsyncTransport,
  transportOptions: {
    fileName: 'appLogs.log',
    fileSize: 512 * 1024, // Опционально: указываем максимальный размер файла
    formatter: (msg) => `[${msg.date.toISOString()}] [${msg.type}] ${msg.message}`, // Опционально: форматирование логов
  },
});

export { log };
