const fs = require('fs');
const { logger } = require('react-native-logs');

// Функция для записи лога в файл
const writeToLogFile = (logMessage) => {
  const logFilePath = 'logs.txt';
  const formattedLog = `[${new Date().toISOString()}] ${logMessage}\n`;

  fs.appendFile(logFilePath, formattedLog, (err) => {
    if (err) {
      console.error('Ошибка при записи лога:', err);
    } else {
      console.log('Лог успешно записан в файл.');
    }
  });
};

// Создание логгера
export const log = logger.createLogger({
  // Можно оставить другие настройки логгера здесь
  // ...

  // Функция для перехвата сообщений перед отправкой в логгер
  logFunction: (msg) => {
    // Записываем сообщение в файл перед отправкой в логгер
    writeToLogFile(msg.message);

    // Отправляем сообщение в логгер
    console.log(msg.message);
  },
});
