import * as React from 'react';
import {useState} from 'react';
import { View, Text, Button, Image, TextInput } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Tasks from './component/Tasks';
import TaskComponent from './component/TaskComponent';


function HomeScreen({navigation}) {

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

      <Text>Begin your path to learning English</Text>
        <Button
        title="Word practice"
        onPress={() => navigation.navigate('Word Practice')}
        />
    </View>
  );
}

//const [content, setContent] = useState()



function WordPractScreen({navigation}) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

      <TaskComponent></TaskComponent>
    </View>

  );
}

function ResultScreen({navigation}) {
  <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

    <Text>The end</Text>

  </View>
}

const Stack = createNativeStackNavigator();

function LogoTitle() {

  return (
    <Image
      style={{ width: 50, height: 50 }}
      source={'https://i.pinimg.com/564x/12/f0/e9/12f0e96fb41bc7e972708e3a6bcceceb.jpg'}
    />
  );
}

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home"
        screenOptions={{
          headerStyle: {
            backgroundColor: '#f4511e',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}

      >



        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ title: 'Home' }} />
        <Stack.Screen 
          name="Word Practice" 
          component={WordPractScreen}
          initialParams={{ itemId: 42 }} />
        <Stack.Screen 
          name="Results"
          component={ResultScreen}
        ></Stack.Screen>
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default {App};
