import * as React from 'react';
import {useState} from 'react';
import { View, Text, Button, Image, TextInput, StyleSheet } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Tasks from './component/Tasks';
import TaskComponent from './component/TaskComponent';
import ResultComponent from './component/ResultComponent';


function HomeScreen({navigation}) {

  return (
    <View style={styles.container}>

      <Text style={styles.header}>Begin your path to learning English</Text>
        <Button
        title="Word practice"
        onPress={() => navigation.navigate('Word Practice', {taskT: "words"})}
        style={styles.button}
        />
        <Button
        title="Riddles"
        onPress={() => navigation.navigate('Word Practice', {taskT: "riddles"})}
        style={styles.button}
        />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ffffff',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    color: '#000000',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#007aff',
    color: '#f194ff',
    borderRadius: 5,
    padding: 10,
    margin: 10,
  },
});



const Stack = createNativeStackNavigator();

function LogoTitle() {

  return (
    <Image
      style={{ width: 50, height: 50 }}
      source={'https://i.pinimg.com/564x/12/f0/e9/12f0e96fb41bc7e972708e3a6bcceceb.jpg'}
    />
  );
}

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home"
        screenOptions={{
          headerStyle: {
            backgroundColor: '#f4511e',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}

      >



        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ title: 'Home' }} />
        <Stack.Screen 
          name="Word Practice" 
          component={TaskComponent}
          initialParams={{ itemId: 42 }} />
        <Stack.Screen 
          name='Results'
          component={ResultComponent}
          options={{ headerLeft: null }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;
