import React, { useEffect, useState } from 'react';
import { View, TextInput, Button, Text } from 'react-native';
import db from '../db/firestore';
import Tasks from './Tasks';

const TaskComponent = () => {
    const [ englishApp_tasks, setEnglishApp_tasks ] = useState<Object[]>();

    const [documents, setDocuments] = useState(null);

    const [inputValue, setInputValue] = useState('');

    let word = '';
    let rightAnswer = '';
    let randomIndex = 0;
    let taskType = '';

    useEffect( ()=>{
      const dataCollection = db.collection('englishApp_tasks')
      /* .get()
        .then(result => result.docs)
        .then(docs => docs.map(doc => ({
            id: doc.id, 
            eng: doc.data().eng, 
            lv: doc.data().lv,
            taskDificulty: doc.data().taskDificulty,
            taskType: doc.data().taskType
        })))
        .then(englishApp_tasks => setEnglishApp_tasks(englishApp_tasks));
        */
        const collectInJSON = dataCollection.onSnapshot((querySnapshot) => {
          const docsFromFirebase = [];
          let index = 0
          querySnapshot.forEach((doc) => {
            docsFromFirebase[index] = doc.data();
            index++;
          });
          console.log(docsFromFirebase)
          setDocuments(docsFromFirebase);
        });

        return () => {
          collectInJSON();
        };
    }, [])
    //console.log(typeof documents)
    



  

  const handleInputChange = (text) => {
    setInputValue(text);
  }

  const handleSubmit = () => {
   
    console.log(inputValue);
  }


  if(documents != null) {
    randomIndex = Math.floor(Math.random() * documents.length);

    word = documents[randomIndex].eng;
    rightAnswer = documents[randomIndex].lv;
    taskType = documents[randomIndex].taskType;

  }

  return (
    
    <View>
        
        <Text>{word}</Text>
      <TextInput
        placeholder="Enter your input here"
        onChangeText={handleInputChange}
        value={inputValue}
      />
      <Button
        title="Submit"
        onPress={handleSubmit}
      />
     
    </View>
  );
};

export default TaskComponent;
