import React, { useEffect, useState } from 'react';
import { View, TextInput, Button, Text } from 'react-native';
import db from '../db/firestore';
import Tasks from './Tasks';

const TaskComponent = () => {
    const [ englishApp_tasks, setEnglishApp_tasks ] = useState<Object[]>();

    useEffect( ()=>{
        db.collection('englishApp_tasks')
        .get()
        .then(result => result.docs)
        .then(docs => docs.map(doc => ({
            id: doc.id, 
            eng: doc.data().eng, 
            lv: doc.data().lv,
            taskDificulty: doc.data().taskDificulty,
            taskType: doc.data().taskType
        })))
        .then(englishApp_tasks => setEnglishApp_tasks(englishApp_tasks))
    }, [])
    if (typeof englishApp_tasks !== 'undefined') {
        console.log(englishApp_tasks);
    }



  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (text) => {
    setInputValue(text);
  }

  const handleSubmit = () => {
    // Do something with the input value, e.g. send it to a server
    console.log(inputValue);
  }


  let word = '';
  let rightAnswer = '';
  englishApp_tasks?.map(task => 
    word = task.eng,
  )
  englishApp_tasks?.map(task => 
    rightAnswer = task.lv
  )

  return (
    
    <View>
        
        <Text>{word}</Text>
        
      <TextInput
        placeholder="Enter your input here"
        onChangeText={handleInputChange}
        value={inputValue}
      />
      <Button
        title="Submit"
        onPress={handleSubmit}
      />
     
    </View>
  );
};

export default TaskComponent;
