const { logger } = require('react-native-logs');

export const log = logger.createLogger({
  transport: (msg) => {
    const logMessage = `[${new Date().toISOString()}] [${msg.type}] ${msg.message}\n`;
    localStorage.setItem('App_log.txt', logMessage); // Сохранение лога в localStorage
  },
});
