import { logger } from 'react-native-logs';

// Функция для записи лога в localStorage
const writeToLocalStorage = (logMessage) => {
  const logs = JSON.parse(localStorage.getItem('logs')) || [];
  logs.push({ timestamp: new Date().toISOString(), message: logMessage });
  localStorage.setItem('logs', JSON.stringify(logs));
};

// Создание логгера
export const log = logger.createLogger({
  // Другие настройки логгера...

  // Функция для перехвата сообщений перед отправкой в логгер
  logFunction: (msg) => {
    // Записываем сообщение в localStorage перед отправкой в логгер
    writeToLocalStorage(msg.message);

    // Отправляем сообщение в логгер
    console.log(msg.message);
  },
});
