import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import db from '../db/firestore';

const Tasks = () => {

    const [ englishApp_tasks, setEnglishApp_tasks ] = useState<Object[]>()

    useEffect( ()=>{
        db.collection('englishApp_tasks')
        .get()
        .then(result => result.docs)
        .then(docs => docs.map(doc => ({
            id: doc.id, 
            eng: doc.data().eng, 
            lv: doc.data().lv,
            taskDificulty: doc.data().taskDificulty,
            taskType: doc.data().taskType
        })))
        .then(englishApp_tasks => setEnglishApp_tasks(englishApp_tasks))
    }, [])


    //show content
    return (
        <View>
        {
            englishApp_tasks?.map(englishApp_task => <View>
                <Text>(englishApp_task.name)</Text>    
            </View>)
        }
            <View>
                <Text>Somethiong</Text>
            </View>
            <View>
                <Text>Somethiong</Text>
            </View>
        </View>
    )
}

export default Tasks;
