import { logger } from 'react-native-logs';

// Функция для сохранения логов в IndexedDB
async function saveLogToIndexedDB(logData) {
  try {
    // Открытие или создание базы данных
    const db = await window.indexedDB.open('MyLogsDB', 1);

    // Создание объектного хранилища
    db.onupgradeneeded = function (event) {
      const database = event.target.result;
      if (!database.objectStoreNames.contains('logs')) {
        const objectStore = database.createObjectStore('logs', { keyPath: 'id', autoIncrement: true });
        objectStore.createIndex('timestamp', 'timestamp', { unique: false });
      }
    };

    // Добавление лога в хранилище
    const transaction = db.transaction('logs', 'readwrite');
    const objectStore = transaction.objectStore('logs');
    const request = objectStore.add(logData);

    request.onsuccess = function () {
      console.log('Log added to IndexedDB');
    };

    request.onerror = function (error) {
      console.error('Error adding log to IndexedDB:', error);
    };
  } catch (error) {
    console.error('IndexedDB error:', error);
  }
}

// Создание логгера
export const log = logger.createLogger({
 
});
