import * as FileSystem from 'expo-file-system';
import { logger, fileAsyncTransport } from 'react-native-logs';

const fileSystem = {
  appendFile: async (filePath, data, encoding) => {
    try {
      await FileSystem.appendAsStringAsync(filePath, data, { encoding });
    } catch (error) {
      console.error("Ошибка записи в файл:", error);
      throw error;
    }
  },
};

export const log = logger.createLogger({
  transport: fileAsyncTransport,
  transportOptions: {
    fileSystem: fileSystem,
    fileName: FileSystem.documentDirectory + 'appLogs.txt',
    formatter: (msg) => `[${msg.date.toISOString()}] [${msg.type}] ${msg.message}`,
  },
});
