import { logger } from 'react-native-logs';
import * as FileSystem from 'expo-file-system';

const logFilePath = 'path/to/log.txt'; // Путь к файлу логов внутри вашего проекта

export const log = logger.createLogger({
  logFunction: async ({ level, message }) => {
    const logMessage = `${message}\n`; // Просто сообщение без форматирования

    try {
      await FileSystem.writeAsStringAsync(logFilePath, logMessage, { encoding: 'utf8', append: true });
      console.log('Log written to file');
    } catch (error) {
      console.error('Error writing log:', error);
    }
  },
  // Другие параметры конфигурации, если необходимо
});
