import * as FileSystem from 'expo-file-system';
import { logger, fileAsyncTransport } from 'react-native-logs';

const appendToFile = async (filePath, data, encoding) => {
  try {
    await FileSystem.writeAsStringAsync(filePath, data, { encoding: encoding, append: true });
  } catch (error) {
    console.error("Ошибка записи в файл:", error);
    throw error;
  }
};

export const log = logger.createLogger({
  transport: async (msg) => {
    const currentDate = msg.date instanceof Date ? msg.date.toISOString() : new Date().toISOString();
    const formattedMessage = `[${currentDate}] [${msg.type}] ${msg.message}`;
    await appendToFile('log.txt', formattedMessage, 'utf8');
  },
});
