import React, { useEffect, useLayoutEffect, useState } from 'react';
import { View, TextInput, Button, Text } from 'react-native';
import db from '../db/firestore';
import Tasks from './Tasks';
//import { useNavigation } from '@react-navigation/native';



const TaskComponent = ({navigation}) => {
    //const navigation = useNavigation();
    const [ englishApp_tasks, setEnglishApp_tasks ] = useState<Object[]>();

    const [documents, setDocuments] = useState(null);

    const [inputValue, setInputValue] = useState(null);

    const [update, setUpdate] = useState(false);

    const [usedIndexes, setUsedIndexes] = useState([]);

    const [score, setScore] = useState(0);
    const [wrongCount, setWrongCount] = useState(0);
    const [word, setWord] = useState('');
    //let rightAnswer = '';
    const [rightAnswer, setRightAnswer] = useState('');
    let randomIndex = 0;
    const [taskType, setTaskType] = useState('');
    //let taskType = '';

    useEffect( ()=>{
      const dataCollection = db.collection('englishApp_tasks')

        const collectInJSON = dataCollection.onSnapshot((querySnapshot) => {
          const docsFromFirebase = [];
          let index = 0
          querySnapshot.forEach((doc) => {
            docsFromFirebase[index] = doc.data();
            index++;
          });
          console.log(docsFromFirebase)
          setDocuments(docsFromFirebase);
        });



        console.log('FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF')
        return () => {
          collectInJSON();
        };
    }, [])
    
  //
  let endThis = false;
  const [end, setEnd] = useState(false);
  useEffect(() => {
    if(documents != null) {

      randomIndex = Math.floor(Math.random() * documents.length);
      let i = 0;
      
      while (true){
      //console.log(i);
        if(!usedIndexes.includes(randomIndex)){
          break;
        }
        
        randomIndex = Math.floor(Math.random() * documents.length);
        if(documents.length < i){
          //console.log(documents.length + 'Loooooool' + i);
          return () => {
            console.log("Run out of tasks");

  
          }
          //break;
        }

        i++;
      }

      
      
      console.log(documents[randomIndex])
      setWord(documents[randomIndex].eng);
      setRightAnswer(documents[randomIndex].lv);
      setTaskType(documents[randomIndex].taskType);
      addUsedIndex(randomIndex);
      
    }
   
    return () => {
      setWord('n');
      console.log("values given")
    }
  }, [documents, update])
  

  //when there are no tasks left, do the thing
  if(word == 'n'){
    console.log('kaput');
    navigation.navigate('Results')           
    
  }

    
  

  const handleInputChange = (text) => {
    setInputValue(text);
  }
  
  

//count time
//tasks count
//task types
//design
//more data in db

/*
myCollection
  .add({
    name: 'John Doe',
    age: 30,
    email: 'john.doe@example.com',
  })
  .then((docRef) => {
    console.log('Document written with ID: ', docRef.id);
  })
  .catch((error) => {
    console.error('Error adding document: ', error);
  });


*/


  useEffect(() => {
    console.log("SCORE: " + score);
    console.log("WRONGCOUNT: " + wrongCount);
  }, [score, wrongCount]);

  const handleSubmit = () => {
   if(inputValue == rightAnswer){
    
    
    setUpdate(!update);
    setInputValue('');
    setScore(score + 1);
    
   }
   if(inputValue != '' && inputValue != rightAnswer) {
    setWrongCount(wrongCount + 1);
   }
  }
  //I populate array of used indexes to check if given task was used during one session
  const addUsedIndex = (index) => {
    setUsedIndexes([...usedIndexes, index]);
    console.log("index added");
  }


  //usedIndexes && console.log(usedIndexes);


  return (
    
    <View>
        
        <Text>{word && word}</Text>
      <TextInput
        placeholder="Enter your input here"
        onChangeText={handleInputChange}
        value={inputValue}
      />
      <Button
        title="Submit"
        onPress={handleSubmit}
      />
     
    </View>
    
  );
};

export default TaskComponent;
