import { logger, fileAsyncTransport } from "react-native-logs";
//import RNFS from "react";


const fileSystem = {
    appendFile: async (filePath, data, encoding) => {
      try {
        await RNFS.appendFile(filePath, data, encoding);
      } catch (error) {
        console.error("Ошибка записи в файл:", error);
        throw error;
      }
    },
  };
  
  export const log = logger.createLogger({
    transport: fileAsyncTransport,
    transportOptions: {
      fileSystem: fileSystem,
      fileName: 'log.txt',
      fileSize: 512 * 1024, // Опционально: указываем максимальный размер файла
      formatter: (msg) => `[${msg.date.toISOString()}] [${msg.type}] ${msg.message}`, // Опционально: форматирование логов
    },
  });
