import * as FileSystem from 'expo-file-system';
import { logger } from 'react-native-logs';

const logFilePath = FileSystem.documentDirectory + 'appLogs.txt';

export const log = logger.createLogger({
  logFunction: ({ level, message }) => {
    const logMessage = `[${new Date().toISOString()}] [${level}] ${message}\n`;
    FileSystem.appendAsStringAsync(logFilePath, logMessage, { encoding: FileSystem.EncodingType.UTF8 })
      .then(() => console.log('Log written to file')) // Опционально: лог об успешной записи
      .catch(error => console.error('Error writing log:', error)); // Опционально: обработка ошибки записи
  },
});
