const fs = require('fs');
const { logger } = require('react-native-logs');

const logFilePath = './logs/log.txt'; // Путь к файлу логов внутри вашего проекта

export const log = logger.createLogger({
  transport: (msg) => {
    const logMessage = `[${new Date().toISOString()}] [${msg.type}] ${msg.message}\n`;
    fs.appendFileSync(logFilePath, logMessage); // Запись сообщения в файл
  },
});
