import { logger } from 'react-native-logs';
import RNFS from 'react-native-fs';

export const log = logger.createLogger({
  transport: (msg) => {
    // Форматируем сообщение лога
    const logMessage = `[${msg.date.toISOString()}] [${msg.type}] ${msg.message}\n`;

    // Путь к файлу логов
    const filePath = `${RNFS.DocumentDirectoryPath}/appLogs.log`;

    // Записываем лог в файл
    RNFS.appendFile(filePath, logMessage, 'utf8')
      .then(() => {
        console.log('Лог успешно записан в файл.');
      })
      .catch((err) => {
        console.error('Ошибка записи в файл:', err);
      });
  },
});
